import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import './Header.css';

function Header() {
  const [username, setUsername] = useState(localStorage.getItem('username'));

  useEffect(() => {
    const syncUsername = () => {
      setUsername(localStorage.getItem('username'));
    };

    window.addEventListener('storage', syncUsername);
    return () => window.removeEventListener('storage', syncUsername);
  }, []);

  const isLoggedIn = !!username;

  return (
    <nav className="navbar navbar-expand-lg navbar-dark bg-dark px-3">
      <Link className="navbar-brand" to="/">üìö BookStore</Link>
      <div className="collapse navbar-collapse">
        <ul className="navbar-nav ms-auto align-items-center">
          {isLoggedIn && (
            <li className="nav-item text-white me-3">
              üëã Hi, <strong>{username}</strong>
            </li>
          )}
          <li className="nav-item">
            <Link className="nav-link" to="/cart">üõí Cart</Link>
          </li>
          <li className="nav-item">
            <Link className="nav-link" to="/orders">üì¶ Orders</Link>
          </li>
          <li className="nav-item">
            <Link className="nav-link" to="/login">üë§ User Login</Link>
          </li>
          <li className="nav-item">
            <Link className="nav-link" to="/owner/login">üîê Admin Login</Link>
          </li>
        </ul>
      </div>
    </nav>
  );
}

export default Header;
