import React, { useEffect, useState } from 'react';
import axios from 'axios';

function Cart() {
  const [cart, setCart] = useState([]);
  const [total, setTotal] = useState(0);
  const userId = localStorage.getItem("userId");

  useEffect(() => {
    if (!userId) return;

    axios.get(`/api/cart/${userId}`)
      .then(res => {
        setCart(res.data);
        calculateTotal(res.data);
      })
      .catch(err => {
        console.error("Failed to load cart:", err);
        alert("Could not load cart. Please try again.");
      });
  }, [userId]);

  const calculateTotal = (cartItems) => {
    const sum = cartItems.reduce((acc, item) => acc + item.book.price * item.quantity, 0);
    setTotal(sum);
  };

  const removeFromCart = (id) => {
    axios.delete(`/api/cart/remove/${id}`).then(() => {
      const updatedCart = cart.filter(item => item.id !== id);
      setCart(updatedCart);
      calculateTotal(updatedCart);
    });
  };

  const updateQuantity = (itemId, delta) => {
    const item = cart.find(i => i.id === itemId);
    const newQuantity = item.quantity + delta;

    if (newQuantity <= 0) {
      removeFromCart(itemId);
      return;
    }

    axios.put(`/api/cart/update/${itemId}`, { quantity: newQuantity })
      .then(() => {
        const updatedCart = cart.map(i =>
          i.id === itemId ? { ...i, quantity: newQuantity } : i
        );
        setCart(updatedCart);
        calculateTotal(updatedCart);
      })
      .catch(err => {
        console.error("Quantity update failed:", err);
        alert("Failed to update quantity.");
      });
  };

  const handleCheckout = async () => {
    try {
      await axios.post('/api/orders/place', null, {
        params: {
          userId: userId,
          totalAmount: total.toFixed(2)
        }
      });
      alert("üéâ Order placed successfully!");
      setCart([]);
      setTotal(0);
    } catch (error) {
      console.error("Checkout failed:", error);
      alert("Failed to place order.");
    }
  };

  return (
    <div className="max-w-2xl mx-auto mt-10 p-6 bg-white rounded-xl shadow-lg">
      <h2 className="text-2xl font-bold mb-6 text-center text-gray-800">üõí Your Cart</h2>

      {cart.length === 0 ? (
        <p className="text-center text-gray-500">Your cart is empty.</p>
      ) : (
        <div className="space-y-4">
          {cart.map(item => (
            <div
              key={item.id}
              className="flex justify-between items-center border-b pb-3"
            >
              <div>
                <h3 className="font-semibold text-gray-800">{item.book.title}</h3>
                <p className="text-sm text-gray-600 mb-1">
                  ‚Çπ{item.book.price.toFixed(2)} x {item.quantity} ={" "}
                  <span className="font-medium text-black">
                    ‚Çπ{(item.book.price * item.quantity).toFixed(2)}
                  </span>
                </p>

                <div className="flex items-center space-x-2">
                  <button
                    onClick={() => updateQuantity(item.id, -1)}
                    className="px-2 py-1 bg-gray-200 rounded hover:bg-gray-300"
                  >
                    ‚àí
                  </button>
                  <span className="px-2">{item.quantity}</span>
                  <button
                    onClick={() => updateQuantity(item.id, 1)}
                    className="px-2 py-1 bg-gray-200 rounded hover:bg-gray-300"
                  >
                    +
                  </button>
                </div>
              </div>

              <button
                onClick={() => removeFromCart(item.id)}
                className="text-red-600 hover:underline text-sm"
              >
                ‚ùå Remove
              </button>
            </div>
          ))}

          <div className="mt-6 text-right">
            <h4 className="text-xl font-bold text-gray-800">
              Total: ‚Çπ{total.toFixed(2)}
            </h4>
            <button
              onClick={handleCheckout}
              className="mt-4 px-6 py-2 bg-green-600 text-white rounded-md hover:bg-green-700 transition"
            >
              ‚úÖ Proceed to Checkout
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default Cart;
