import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function OwnerLogin() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    if (username === 'sripriya' && password === '4453') {
      setIsAuthenticated(true);
    } else {
      alert('Invalid credentials!');
    }
  };

  const goTo = (path) => navigate(path);

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100 p-4">
      <div className="w-full max-w-md bg-white shadow-lg rounded-lg p-6">
        {!isAuthenticated ? (
          <>
            <h2 className="text-2xl font-bold text-center mb-6">üîê Owner Login</h2>
            <form onSubmit={handleSubmit} className="space-y-4">
              <input
                type="text"
                placeholder="Username"
                className="w-full border rounded px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-400"
                onChange={(e) => setUsername(e.target.value)}
                required
              />
              <input
                type="password"
                placeholder="Password"
                className="w-full border rounded px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-400"
                onChange={(e) => setPassword(e.target.value)}
                required
              />
              <button
                type="submit"
                className="w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700 transition"
              >
                Login
              </button>
            </form>
          </>
        ) : (
          <>
            <h2 className="text-2xl font-bold text-center mb-4">üëã Welcome, {username}!</h2>
            <div className="space-y-3">
              <button
                className="w-full bg-green-600 text-white py-2 rounded hover:bg-green-700 transition"
                onClick={() => goTo('/admin/add-book')}
              >
                ‚ûï Add Book
              </button>
              <button
                className="w-full bg-red-600 text-white py-2 rounded hover:bg-red-700 transition"
                onClick={() => goTo('/admin/manage-books')}
              >
                üóëÔ∏è View/Delete Books
              </button>
              <button
                className="w-full bg-gray-600 text-white py-2 rounded hover:bg-gray-700 transition"
                onClick={() => goTo('/')}
              >
                üîô Exit Dashboard
              </button>
            </div>
          </>
        )}
      </div>
    </div>
  );
}

export default OwnerLogin;
