import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Cart = ({ userId }) => {
  const [cartItems, setCartItems] = useState([]);

  useEffect(() => {
    if (userId) fetchCart();
  }, [userId]);

  const fetchCart = async () => {
    try {
      const response = await axios.get(`/api/cart/${userId}`);
      setCartItems(response.data);
    } catch (error) {
      console.error('Error fetching cart:', error);
    }
  };

  const updateQuantity = async (cartItemId, quantity) => {
    if (quantity < 1) return;
    try {
      await axios.put(`/api/cart/update?cartItemId=${cartItemId}&quantity=${quantity}`);
      setCartItems(prev =>
        prev.map(item =>
          item.id === cartItemId ? { ...item, quantity } : item
        )
      );
    } catch (error) {
      console.error('Error updating quantity:', error);
    }
  };

  const removeItem = async (id) => {
    try {
      await axios.delete(`/api/cart/remove/${id}`);
      setCartItems(prev => prev.filter(item => item.id !== id));
    } catch (error) {
      console.error('Error removing item:', error);
    }
  };

  return (
    <div className="container my-4">
      <h2 className="mb-4">ðŸ›’ Your Cart</h2>
      {cartItems.length === 0 ? (
        <p className="text-muted">Your cart is empty.</p>
      ) : (
        <div className="row g-3">
          {cartItems.map(item => (
            <div key={item.id} className="col-md-6">
              <div className="card shadow-sm p-3 d-flex flex-row align-items-center">
                <img
                  src={item.book.imageUrl || '/default-book.jpg'}
                  alt={item.book.title}
                  className="me-3"
                  style={{ width: '70px', height: '100px', objectFit: 'cover' }}
                />
                <div className="flex-grow-1">
                  <h5>{item.book.title}</h5>
                  <p className="mb-1 text-muted">â‚¹{item.book.price}</p>
                  <div className="d-flex align-items-center gap-2">
                    <button
                      className="btn btn-outline-secondary btn-sm"
                      onClick={() => updateQuantity(item.id, item.quantity - 1)}
                    >âˆ’</button>
                    <span>{item.quantity}</span>
                    <button
                      className="btn btn-outline-secondary btn-sm"
                      onClick={() => updateQuantity(item.id, item.quantity + 1)}
                    >+</button>
                    <button
                      className="btn btn-outline-danger btn-sm ms-auto"
                      onClick={() => removeItem(item.id)}
                    >Remove</button>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Cart;
