import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './BookList.css'; // Import custom CSS
import { useNavigate } from 'react-router-dom';

function BookList() {
  const [books, setBooks] = useState([]);
  const navigate = useNavigate();
  const userId = 1; // replace with dynamic ID if needed

  useEffect(() => {
    axios.get('/api/books').then(res => setBooks(res.data));
  }, []);

  const addToCart = async (bookId) => {
    try {
      await axios.post(`/api/cart/add`, {
        userId: userId,
        bookId: bookId,
        qty: 1,
      });
      alert('Book added to cart!');
    } catch (err) {
      alert('Failed to add book to cart.');
    }
  };

  return (
    <div className="book-grid-container">
      <h2 className="mb-4">ðŸ“š Browse Books</h2>
      <div className="book-grid">
        {books.map(book => (
          <div className="book-card" key={book.id}>
            <img
              src={book.imageUrl || 'https://via.placeholder.com/150'}
              alt={book.title}
              className="book-img"
            />
            <h5 className="book-title">{book.title}</h5>
            <p className="book-author">{book.author}</p>
            <p className="book-price">â‚¹{book.price.toFixed(2)}</p>
            <button className="btn btn-primary" onClick={() => addToCart(book.id)}>
              ðŸ›’ Add to Cart
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default BookList;
