import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { FaTrash, FaShoppingCart } from 'react-icons/fa'; // Optional

function Cart() {
  const [cart, setCart] = useState([]);
  const [total, setTotal] = useState(0);
  const userId = localStorage.getItem("userId");

  useEffect(() => {
    if (!userId) return;

    axios.get(`/api/cart/${userId}`)
      .then(res => {
        setCart(res.data);
        const sum = res.data.reduce((acc, item) => acc + item.book.price * item.quantity, 0);
        setTotal(sum);
      })
      .catch(err => {
        console.error("Failed to load cart:", err);
        alert("Could not load cart. Please try again.");
      });
  }, [userId]);

  const removeFromCart = (id) => {
    axios.delete(`/api/cart/remove/${id}`)
      .then(() => {
        setCart(prev => prev.filter(item => item.id !== id));
      });
  };

  const handleCheckout = async () => {
    try {
      await axios.post('/api/orders/place', null, {
        params: {
          userId,
          totalAmount: total.toFixed(2)
        }
      });
      alert("ðŸŽ‰ Order placed successfully!");
      setCart([]);
      setTotal(0);
    } catch (error) {
      console.error("Checkout failed:", error);
      alert("Failed to place order.");
    }
  };

  return (
    <div className="container mt-4">
      <h2 className="mb-4 text-primary">
        <FaShoppingCart className="me-2" />Your Cart
      </h2>

      {cart.length === 0 ? (
        <div className="alert alert-info">ðŸ›’ Your cart is currently empty.</div>
      ) : (
        <>
          <div className="row">
            {cart.map(item => (
              <div key={item.id} className="col-md-6 mb-3">
                <div className="card shadow-sm">
                  <div className="card-body d-flex justify-content-between align-items-center">
                    <div>
                      <h5 className="card-title mb-1">{item.book.title}</h5>
                      <p className="card-text mb-0">â‚¹{item.book.price.toFixed(2)} x {item.quantity}</p>
                      <strong>Total: â‚¹{(item.book.price * item.quantity).toFixed(2)}</strong>
                    </div>
                    <button
                      className="btn btn-outline-danger btn-sm"
                      onClick={() => removeFromCart(item.id)}
                    >
                      <FaTrash /> Remove
                    </button>
                  </div>
                </div>
              </div>
            ))}
          </div>

          <div className="text-end mt-4">
            <h4 className="text-success">Grand Total: â‚¹{total.toFixed(2)}</h4>
            <button className="btn btn-success mt-3 px-4 py-2" onClick={handleCheckout}>
              âœ… Proceed to Checkout
            </button>
          </div>
        </>
      )}
    </div>
  );
}

export default Cart;
