import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './BookList.css';
import { useNavigate } from 'react-router-dom';

function BookList() {
  const [books, setBooks] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    axios.get('/api/books')
      .then(res => setBooks(res.data))
      .catch(() => alert("Failed to load books"));
  }, []);

  const addToCart = async (bookId) => {
    const storedUserId = localStorage.getItem("userId");

    if (!storedUserId || storedUserId === "undefined" || storedUserId === "null") {
      alert("Please log in to add books to your cart.");
      navigate('/login');
      return;
    }

    try {
      await axios.post('/api/cart/add', null, {
        params: {
          userId: storedUserId,
          bookId: bookId,
          quantity: 1
        }
      });
      alert('Book added to cart!');
    } catch (err) {
      console.error("Add to Cart Error:", err);
      alert('Failed to add book to cart.');
    }
  };

  return (
    <div className="container my-5">
      <h2 className="text-center mb-4 fw-bold">ðŸ“– Browse Our Collection</h2>
      <div className="row">
        {books.map(book => (
          <div className="col-md-6 col-lg-4 mb-4" key={book.id}>
            <div className="card h-100 shadow-sm border-0 book-card-hover">
              <img
                src={book.imageUrl || 'https://via.placeholder.com/150'}
                alt={book.title}
                className="card-img-top"
                style={{ height: '250px', objectFit: 'cover' }}
              />
              <div className="card-body d-flex flex-column">
                <h5 className="card-title text-primary">{book.title}</h5>
                <p className="card-text text-muted mb-1"><strong>Author:</strong> {book.author}</p>
                <p className="card-text fw-semibold mb-3">Price: â‚¹{book.price.toFixed(2)}</p>
                <button
                  className="btn btn-outline-primary mt-auto"
                  onClick={() => addToCart(book.id)}
                >
                  ðŸ›’ Add to Cart
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default BookList;
