import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function OwnerLogin() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    if (username === 'sripriya' && password === '4453') {
      setIsAuthenticated(true);
    } else {
      alert('Invalid credentials!');
    }
  };

  const handleNavigate = (path) => {
    navigate(path);
  };

  return (
    <div className="container">
      <h2>Owner Login</h2>
      {!isAuthenticated ? (
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            placeholder="Username"
            className="form-control mb-2"
            onChange={(e) => setUsername(e.target.value)}
          />
          <input
            type="password"
            placeholder="Password"
            className="form-control mb-2"
            onChange={(e) => setPassword(e.target.value)}
          />
          <button className="btn btn-primary">Login</button>
        </form>
      ) : (
        <div className="mt-4">
          <h4>Welcome, {username}!</h4>
          <button className="btn btn-success m-2" onClick={() => handleNavigate('/admin/add-book')}>
            ‚ûï Add Book
          </button>
          <button className="btn btn-danger m-2" onClick={() => handleNavigate('/admin/delete-book')}>
            üóëÔ∏è Delete Book
          </button>
          <button className="btn btn-secondary m-2" onClick={() => handleNavigate('/admin/view-books')}>
            üìö View Books
          </button>
        </div>
      )}
    </div>
  );
}

export default OwnerLogin;
