import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './Header.css';

function Header() {
  const username = localStorage.getItem('username');
  const isLoggedIn = localStorage.getItem('userId') !== null;
  const navigate = useNavigate();

  const [searchTerm, setSearchTerm] = useState('');

  const handleLogout = () => {
    localStorage.clear();
    navigate('/login');
    window.location.reload();
  };

  const handleSearch = (e) => {
  e.preventDefault();
  const trimmed = searchTerm.trim();
  if (trimmed !== '') {
    navigate(`/search?q=${encodeURIComponent(trimmed)}`);
    setSearchTerm('');
  }
};

  return (
    <nav className="navbar navbar-expand-lg navbar-dark bg-dark px-3">
      <Link className="navbar-brand" to="/">üìö BookStore</Link>

      <form className="d-flex mx-auto" onSubmit={handleSearch}>
        <input
          type="text"
          className="form-control me-2"
          placeholder="üîç Search books by name..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <button type="submit" className="btn btn-outline-light">Search</button>
      </form>

      <div className="collapse navbar-collapse">
        <ul className="navbar-nav ms-auto align-items-center">
          {isLoggedIn && (
            <>
              <li className="nav-item text-white me-3">
                üëã Hi, <strong>{username}</strong>
              </li>
              <li className="nav-item">
                <button className="btn btn-outline-light" onClick={handleLogout}>
                  üîì Logout
                </button>
              </li>
            </>
          )}
          {!isLoggedIn && (
            <>
              <li className="nav-item">
                <Link className="nav-link" to="/login">üë§ User Login</Link>
              </li>
              <li className="nav-item">
                <Link className="nav-link" to="/owner/login">üîê Admin Login</Link>
              </li>
            </>
          )}
          <li className="nav-item">
            <Link className="nav-link" to="/cart">üõí Cart</Link>
          </li>
          <li className="nav-item">
            <Link className="nav-link" to="/orders">üì¶ Orders</Link>
          </li>
        </ul>
      </div>
    </nav>
  );
}

export default Header;
