import React, { useEffect, useState } from "react";
import axios from "axios";

export default function Orders() {
  const [orders, setOrders] = useState([]);
  const userId = localStorage.getItem("userId");

  useEffect(() => {
  if (!userId) return;

  axios.get(`/api/orders/user/${userId}`)
    .then(res => {
      console.log("üì¶ Orders response:", res.data);
      if (Array.isArray(res.data)) {
        setOrders(res.data);
      } else {
        console.warn("Expected array but got:", res.data);
        setOrders([]); // fallback to empty array
      }
    })
    .catch(err => {
      console.error("‚ùå Failed to load orders:", err);
      alert("Could not fetch your orders. Please make sure you're logged in.");
    });
}, [userId]);


  return (
    <div className="container mt-4">
      <h2 className="mb-4">My Orders</h2>
      {orders.length > 0 ? (
        orders.map(order => (
          <div key={order.id} className="card mb-3">
            <div className="card-body">
              <h5 className="card-title">Order #{order.id}</h5>
              <p className="card-text">
                <strong>Date:</strong> {order.orderDate ? new Date(order.orderDate).toLocaleString() : "N/A"}<br />
                <strong>Total:</strong> ‚Çπ{order.totalAmount?.toFixed(2) || "0.00"}
              </p>
              <ul className="list-group list-group-flush">
                {order.items?.map(item => (
                  <li key={item.id} className="list-group-item">
                    {item.book?.title || "Unknown Book"} √ó {item.quantity}
                  </li>
                )) || <li className="list-group-item">No items</li>}
              </ul>
            </div>
          </div>
        ))
      ) : (
        <p>No orders found.</p>
      )}
    </div>
  );
}
