import React from 'react';
import axios from 'axios';
import './BookList.css'; // reuse the same CSS
import { useNavigate } from 'react-router-dom';

function SearchResults({ results }) {
  const navigate = useNavigate();
  const userId = localStorage.getItem("userId");

  const addToCart = async (bookId) => {
    const storedUserId = localStorage.getItem("userId");

    if (!storedUserId || storedUserId === "undefined" || storedUserId === "null") {
      alert("Please log in to add books to your cart.");
      navigate('/login');
      return;
    }

    try {
      await axios.post('/api/cart/add', null, {
        params: {
          userId: storedUserId,
          bookId: bookId,
          quantity: 1
        }
      });
      alert('Book added to cart!');
    } catch (err) {
      console.error("Add to Cart Error:", err);
      alert('Failed to add book to cart.');
    }
  };

  if (!results || results.length === 0) {
    return <h4 className="text-center mt-5">No books found for your search üîç</h4>;
  }

  return (
    <div className="book-grid-container">
      <h2 className="mb-4">üîç Search Results</h2>
      <div className="book-grid">
        {results.map(book => (
          <div className="book-card" key={book.id}>
            <img
              src={book.imageUrl || 'https://via.placeholder.com/150'}
              alt={book.title}
              className="book-img"
            />
            <h5 className="book-title">{book.title}</h5>
            <p className="book-author">{book.author}</p>
            <p className="book-price">‚Çπ{book.price.toFixed(2)}</p>
            <button className="btn btn-primary" onClick={() => addToCart(book.id)}>
              üõí Add to Cart
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default SearchResults;
