import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Cart = ({ userId }) => {
  const [cartItems, setCartItems] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (userId) fetchCart();
  }, [userId]);

  const fetchCart = async () => {
    try {
      const response = await axios.get(`/api/cart/${userId}`);
      setCartItems(response.data);
    } catch (error) {
      console.error('❌ Error fetching cart:', error);
    } finally {
      setLoading(false);
    }
  };

  const updateQuantity = async (cartItemId, newQuantity) => {
    if (newQuantity < 1) return;

    try {
      await axios.put(`/api/cart/update`, null, {
        params: { cartItemId, quantity: newQuantity }
      });
      setCartItems(prev =>
        prev.map(item =>
          item.id === cartItemId ? { ...item, quantity: newQuantity } : item
        )
      );
    } catch (error) {
      console.error('❌ Error updating quantity:', error);
    }
  };

  const removeItem = async (id) => {
    try {
      await axios.delete(`/api/cart/remove/${id}`);
      setCartItems(prev => prev.filter(item => item.id !== id));
    } catch (error) {
      console.error('❌ Error removing item:', error);
    }
  };

  if (loading) return <p className="text-center my-5">Loading cart...</p>;

  return (
    <div className="container my-5">
      <h2 className="mb-4">🛒 Your Cart</h2>

      {cartItems.length === 0 ? (
        <p className="text-muted">Your cart is empty.</p>
      ) : (
        <div className="row g-4">
          {cartItems.map(item => (
            <div key={item.id} className="col-md-6 col-lg-4">
              <div className="card shadow-sm h-100">
                <div className="d-flex p-3 align-items-start gap-3">
                  <img
                    src={item.book?.imageUrl || '/default-book.jpg'}
                    alt={item.book?.title}
                    style={{ width: '80px', height: '110px', objectFit: 'cover' }}
                  />
                  <div className="flex-grow-1">
                    <h5 className="card-title">{item.book?.title}</h5>
                    <p className="text-muted mb-1">Price: ₹{item.book?.price}</p>
                    <div className="d-flex align-items-center mt-2 gap-2">
                      <button
                        className="btn btn-outline-secondary btn-sm"
                        onClick={() => updateQuantity(item.id, item.quantity - 1)}
                      >−</button>
                      <span>{item.quantity}</span>
                      <button
                        className="btn btn-outline-secondary btn-sm"
                        onClick={() => updateQuantity(item.id, item.quantity + 1)}
                      >+</button>
                    </div>
                    <button
                      className="btn btn-outline-danger btn-sm mt-2"
                      onClick={() => removeItem(item.id)}
                    >Remove</button>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Cart;
