import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function OwnerLogin() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    if (username === 'sripriya' && password === '4453') {
      setIsAuthenticated(true);
    } else {
      alert('‚ùå Invalid credentials!');
    }
  };

  const goTo = (path) => navigate(path);

  return (
    <div className="d-flex justify-content-center align-items-center vh-100 bg-light">
      {!isAuthenticated ? (
        <div className="card p-4 shadow-lg" style={{ width: '100%', maxWidth: '400px' }}>
          <h4 className="text-center mb-4">üîê Owner Login</h4>
          <form onSubmit={handleSubmit}>
            <div className="mb-3">
              <label className="form-label">Username</label>
              <input
                type="text"
                className="form-control"
                placeholder="Enter username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                required
              />
            </div>
            <div className="mb-3">
              <label className="form-label">Password</label>
              <input
                type="password"
                className="form-control"
                placeholder="Enter password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
            </div>
            <div className="d-grid">
              <button type="submit" className="btn btn-primary">Login</button>
            </div>
          </form>
        </div>
      ) : (
        <div className="card p-4 shadow-lg text-center" style={{ width: '100%', maxWidth: '400px' }}>
          <h4 className="mb-4">‚úÖ Welcome, {username}!</h4>
          <div className="d-grid gap-3">
            <button className="btn btn-success" onClick={() => goTo('/admin/add-book')}>
              ‚ûï Add Book
            </button>
            <button className="btn btn-danger" onClick={() => goTo('/admin/manage-books')}>
              üìö View/Delete Books
            </button>
            <button className="btn btn-secondary" onClick={() => goTo('/')}>
              üîô Exit Dashboard
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default OwnerLogin;
