import React, { useEffect, useState } from 'react';
import axios from 'axios';

function ManageBooks() {
  const [books, setBooks] = useState([]);

  const fetchBooks = () => {
    axios.get('/api/books')
      .then(res => setBooks(res.data))
      .catch(err => alert('Failed to load books.'));
  };

  const handleDelete = (id) => {
    if (window.confirm('Are you sure you want to delete this book?')) {
      axios.delete(`/api/books/${id}`)
        .then(() => {
          alert('Book deleted successfully');
          fetchBooks(); // Refresh the list
        })
        .catch(() => alert('Error deleting book'));
    }
  };

  useEffect(() => {
    fetchBooks();
  }, []);

  return (
    <div className="container mt-4">
      <h2>ðŸ“š Manage Books</h2>
      {books.length === 0 ? (
        <p>No books available.</p>
      ) : (
        <div className="row">
          {books.map(book => (
            <div key={book.id} className="col-md-4 mb-4">
              <div className="card h-100">
                <img src={book.imageUrl} className="card-img-top" alt={book.title} />
                <div className="card-body">
                  <h5 className="card-title">{book.title}</h5>
                  <p className="card-text">{book.author}</p>
                  <p><strong>â‚¹{book.price}</strong></p>
                  <button className="btn btn-danger" onClick={() => handleDelete(book.id)}>
                    Delete
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default ManageBooks;
